INSERT INTO TB_RECOMMEND (RECOMMEND_NO, RECOMMEND_TITLE, RECOMMEND_CONTENT, RECOMMEND_ENROLL, RECOMMEND_MODIFY, RECOMMEND_COUNT, RECOMMEND_LIKE, RECOMMEND_STATUS, RECOMMEND_THUMB, ADMIN_NO) 
VALUES (1, '서울 소개', '<p><span style="font-size: 1rem;">서울은 대한민국의 수도입니다. 서울 안에 자리한 사원과 궁전이 이 도시의 길고 깊은 역사를 보여줍니다. 이곳에서는 아름다운 자연과 현대적인 고층 빌딩이 만나 과거와 현재가 융합된 도시를 형성합니다. 경복궁과 창덕궁은 오래된 고궁이며, 북촌한옥마을과 남산골 한옥마을은 한국의 전통이 잘 보존된 마을입니다. 서울은 쇼핑의 천국이기도 합니다. 명동, 동대문시장, 신사동, 삼청동으로 가서 의류, 보석, 화장품을 비롯한 온갖 종류의 최신 패션 상품을 만나보세요. 밤이 되면 서울은 더 화려해집니다. N 서울 타워 정상에 올라 눈부시게 빛나는 서울의 야경을 감상하며 눈 호강을 해보세요. 한강은 수많은 한국 드라마에서 배경으로 등장하는 단골손님입니다. 서울의 맛있는 음식 또한 놓쳐선 안 되겠죠. 비빔밥, 김치, 삼계탕(인삼을 넣은 한국식 치킨 수프) 등 저마다 개성을 뽐내는 음식은 모두 맛있습니다.</span><br></p>', to_date('20/10/06', 'RR/MM/DD'), to_date('20/10/06', 'RR/MM/DD'), 50.0, 23.0, 'Y', 'resources/reco_thumb_upfiles/2020100621282927660.jpg', 1.0);

INSERT INTO TB_RECOMMEND (RECOMMEND_NO, RECOMMEND_TITLE, RECOMMEND_CONTENT, RECOMMEND_ENROLL, RECOMMEND_MODIFY, RECOMMEND_COUNT, RECOMMEND_LIKE, RECOMMEND_STATUS, RECOMMEND_THUMB, ADMIN_NO) 
VALUES (2, '서울 인기 명소  베스트 추천(1) - 명동', '<p><span style="font-size: 1rem;">연중무휴</span><br></p><p><span style="font-size: 1rem;">추천 관광시간 : 3-4시간</span></p><p>주소: 서울 중구</p><p>서울의 중심에 위치한 명동은 대형 백화점의 본점부터 보세 가게, 화장품 로드샵 등 다양한 상점들이 밀집된 쇼핑의 중심지이자, 다양한 길거리 음식을 파는 포차들이 가득한 서울의 번화가입니다. 특히 이곳에 위치한 명동 교자와 명동 칼국수는 명동에서 꼭 먹어봐야 할 맛집으로, 특히 명동 교자는 미슐랭 빕 구르망에 선정된 식당이기도 합니다. 이 밖에도 설렁탕, 냉면집부터 뷔페, 이탈리안, 중식까지 모두 있답니다. 명동에 방문한다면 우리나라에서 가장 오래된 가톨릭 성당인 명동 성당에 방문해보는 걸 추천해드려요. 조용한 성당을 산책하다 보면 어느새 마음이 편안해지는 걸 느낄 수 있답니다.</p>', to_date('20/10/06', 'RR/MM/DD'), to_date('20/10/06', 'RR/MM/DD'), 30.0, 11.0, 'Y', 'resources/reco_thumb_upfiles/2020100621293965112.jpg', 1.0);

INSERT INTO TB_RECOMMEND (RECOMMEND_NO, RECOMMEND_TITLE, RECOMMEND_CONTENT, RECOMMEND_ENROLL, RECOMMEND_MODIFY, RECOMMEND_COUNT, RECOMMEND_LIKE, RECOMMEND_STATUS, RECOMMEND_THUMB, ADMIN_NO) 
VALUES (3, '서울 인기 명소  베스트 추천(2) - N서울타워', '<p><span style="font-size: 1rem;">1/1-12/31 월요일-금요일,일요일 10:00-23:00 영업</span><br></p><blockquote>1/1-12/31 토요일 10:00-00:00 영업<br>추천 관광시간 : 1시간<br>주소: 서울특별시 용산구 용산2가동 남산공원길 105<br>공식 사이트: http://www.nseoultower.co.kr<br>전화번호 +82-2-34559277</blockquote><p>N서울타워는 서울 남산 정상에 있는 서울의 대표 랜드마크로 서울을 한눈에 내려다볼 수 있는 명소입니다. N서울타워에서 가장 인기 있는 곳은 사랑의 자물쇠 울타리로 커플들의 데이트 장소로도 가장 인기가 많은 곳입니다. 사랑하는 연인과 함께 이곳에서 자물쇠를 걸며 영원한 사랑을 약속해보세요. 타워의 가장 높은 곳에 있는 프렌치 레스토랑에서는 서울의 아름다운 뷰를 감상하며 로맨틱한 저녁 식사를 즐기실 수도 있답니다. 계절에 따라 불빛이 다양하게 변하는 N서울타워의 모습도 잊지 말고 감상해보세요.</p>', to_date('20/10/06', 'RR/MM/DD'), to_date('20/10/06', 'RR/MM/DD'), 20.0, 6.0, 'Y', 'resources/reco_thumb_upfiles/2020100621353372452.jpg', 1.0);

INSERT INTO TB_RECOMMEND (RECOMMEND_NO, RECOMMEND_TITLE, RECOMMEND_CONTENT, RECOMMEND_ENROLL, RECOMMEND_MODIFY, RECOMMEND_COUNT, RECOMMEND_LIKE, RECOMMEND_STATUS, RECOMMEND_THUMB, ADMIN_NO) 
VALUES (4, '서울 인기 명소 베스트 추천(3) - 경복궁', '<p><span style="font-size: 1rem;">월요일,수요일-일요일 09:00-18:30 영업</span><br></p><p>9/2-12/4 수요일-금요일 19:30-21:30 영업</p><p>추천 관광시간 : 2시간</p><p>주소: 서울 종로구 사직로 161</p><p>공식 사이트: http://www.royalpalace.go.kr/</p><p>전화번호 +82-2-37003900</p><p><br></p><p>경복궁은 서울 중앙부에 위치해 있으며 조선시대에 건립된 최초의 궁궐입니다. 경복궁은 일년 내내 아름다운 경관을 자랑하는데, 봄에는 벚꽃이, 여름에는 푸르른 나무가, 가을에는 은행나무가, 그리고 겨울에는 백옥같이 하얀 눈이 참으로 아름답습니다. "대장금", "옥탑방 왕세자" 및 "별에서 온 그대" 등의 한국 드라마가 모두 이곳에서 촬영되었습니다. 경복궁의 동쪽에는 건춘문이, 서쪽에는 영춘문이, 북쪽에는 신무문이, 그리고 남쪽에는 광화문이 있습니다. 광화문은 궁궐로 들어가는 주 출입구로, 정교한 장인 정신, 뛰어난 건축 양식 및 아름답고 스타일리시한 디자인이 돋보입니다. 그 중에서도 수문장 교대의식은 놓치지 말아야할 볼거리입니다.</p>', to_date('20/10/06', 'RR/MM/DD'), to_date('20/10/06', 'RR/MM/DD'), 10.0, 2.0, 'Y', 'resources/reco_thumb_upfiles/2020100621533141993.jpg', 1.0);

INSERT INTO TB_RECOMMEND (RECOMMEND_NO, RECOMMEND_TITLE, RECOMMEND_CONTENT, RECOMMEND_ENROLL, RECOMMEND_MODIFY, RECOMMEND_COUNT, RECOMMEND_LIKE, RECOMMEND_STATUS, RECOMMEND_THUMB, ADMIN_NO) 
VALUES (5, '서울 인기 명소 베스트 추천(4) - 북촌한옥마을', '<p><span style="font-size: 1rem;">월요일-토요일 10:00-17:00 영업</span><br></p><p>추천 관광시간 : 2-3시간</p><p>주소: 서울 종로구 계동길 37 (우) 03059</p><p>공식 사이트: http://hanok.seoul.go.kr/</p><p>전화번호 +82-2-21484160 02-2133-1372</p><p><br></p><p>북촌 한옥마을은 서울의 전통 주거 양식이 보존된 곳이자 조선 시대의 한옥 모습을 잘 보존하고 있는 마을입니다.</p><p>이곳에는 우리나라 전통 한옥뿐만 아니라, 정갈한 한식당, 한복 체험을 할 수 있는 한복 가게, 전통 수공예 공방부터 아기자기한 한옥 카페, 분위기 좋은 레스토랑, 갤러리까지 모두 있어 걷기 좋은 동네입니다. 북촌 8경을 따라 한옥의 매력과 서울의 또 다른 모습을 구경해보세요.</p><p>북촌에서는 한옥 스테이도 체험해 볼 수 있답니다. 고즈넉한 멋과 정취를 느낄 수 있는 한옥에서 특별한 하루를 보내보세요. 서울에서 만나는 가장 한국적인 모습을 발견해보세요.</p>', to_date('20/10/06', 'RR/MM/DD'), to_date('20/10/06', 'RR/MM/DD'), 80.0, 32.0, 'Y', 'resources/reco_thumb_upfiles/2020100622003969581.jpg', 1.0);

INSERT INTO TB_RECOMMEND (RECOMMEND_NO, RECOMMEND_TITLE, RECOMMEND_CONTENT, RECOMMEND_ENROLL, RECOMMEND_MODIFY, RECOMMEND_COUNT, RECOMMEND_LIKE, RECOMMEND_STATUS, RECOMMEND_THUMB, ADMIN_NO) 
VALUES (6, '서울 인기 명소 베스트 추천(5) - 청계천', '<p><span style="font-size: 1rem;">연중무휴</span><br></p><p>추천 관광시간 : 1-2시간</p><p>주소: 서울특별시 종로구 창신동</p><p>전화번호 +82-2-22907111</p><p><br></p><p>서울을 가로지르며 흐르는 청계천은 잘 조성된 산책로로 시민들과 서울을 여행하는 여행객들의 편안한 휴식처가 되어주는 곳입니다. 게다가 광화문, 종로, 을지로, 시청, 서울역, 서대문, 경복궁과 같은 명소와 가까우니, 여행으로 지쳐있다면 이곳에 들려 시원한 천에 발을 담그고 잠깐의 휴식도 취할 수 있답니다.</p><p>무더운 여름밤 예쁜 조명이 켜진 청계천을 따라 시원한 바람을 맞으며 연인과 함께하는 산책 데이트는 하루를 더욱 로맨틱하게 만들어 준답니다.</p>', to_date('20/10/06', 'RR/MM/DD'), to_date('20/10/06', 'RR/MM/DD'), 20.0, 8.0, 'Y', 'resources/reco_thumb_upfiles/2020100622040091477.jpg', 1.0);

INSERT INTO TB_RECOMMEND (RECOMMEND_NO, RECOMMEND_TITLE, RECOMMEND_CONTENT, RECOMMEND_ENROLL, RECOMMEND_MODIFY, RECOMMEND_COUNT, RECOMMEND_LIKE, RECOMMEND_STATUS, RECOMMEND_THUMB, ADMIN_NO) 
VALUES (7, '서울 인기 명소 베스트 추천(6) - 여의도', '<p><span style="font-size: 1rem;">연중무휴</span><br></p><p>추천 관광시간 : 1-2시간</p><p>주소: 서울특별시 영등포구 여의동</p><p><br></p><p>여의도는 대한민국 서울에 흐르는 한강에 있는 작은 섬입니다. MBC와 KBS의 본사가 이곳에 있죠. 해마다 봄이 오고 벚꽃이 만개하면 여의도는 서울의 꽃 정원으로 거듭납니다. 여의도를 도는 6km 길이의 도로 양쪽에 벚꽃이 가득 피면 길고 긴 원형 터널을 형성하는데 그 유명한 "벚꽃 길"이 장관을 이룹니다.</p>', to_date('20/10/06', 'RR/MM/DD'), to_date('20/10/06', 'RR/MM/DD'), 26.0, 6.0, 'Y', 'resources/reco_thumb_upfiles/2020100622053372846.jpg', 1.0);

INSERT INTO TB_RECOMMEND (RECOMMEND_NO, RECOMMEND_TITLE, RECOMMEND_CONTENT, RECOMMEND_ENROLL, RECOMMEND_MODIFY, RECOMMEND_COUNT, RECOMMEND_LIKE, RECOMMEND_STATUS, RECOMMEND_THUMB, ADMIN_NO) 
VALUES (8, '서울 인기 명소 베스트 추천(7) - 창덕궁', '<p><span style="font-size: 1rem;">1/1-1/31 화요일-일요일 09:00-16:30 영업;</span><br></p><p>2/1-5/31 화요일-일요일 09:00-18:00 영업;</p><p>6/1-8/31 화요일-일요일 09:00-18:30 영업;</p><p>9/1-10/31 화요일-일요일 09:00-18:00 영업;</p><p>11/1-12/31 화요일-일요일 09:00-16:30 영업</p><p><br></p><p>추천 관광시간 : 1시간</p><p>주소: 서울 종로구 율곡로 99 (우) 03072</p><p>공식 사이트: http://www.cdg.go.kr</p><p>전화번호 +82-2-36682300</p><p><br></p><p>15세기 초반에 태종은 상서로운 장소에 새로운 궁전을 짓도록 명령했다. 새 궁궐을 위해 궁전 건축부서가 새로 창설되었고, 새 건물들은 58 헥타르 면적의 울퉁불퉁한 지형에 맞춰서 건축 되었다. 그 결과로 주변 풍경과 조화를 잘 이룬 동양식 궁궐 건축 양식의 좋은 예가 탄생하게 되고, 1997년 유네스코 세계문화유산에 등록되었다.</p>', to_date('20/10/06', 'RR/MM/DD'), to_date('20/10/06', 'RR/MM/DD'), 32.0, 12.0, 'Y', 'resources/reco_thumb_upfiles/2020100622065533994.jpg', 1.0);

